import React from 'react';
import Head from 'next/head'

const JumboCard = (props) => {
  return (
    <div className="mainContainer">
      <div className="jumboContainer" style={{display: "inline-block", background: "white"}}>
          <div>
              <div>
              <img src="/Temasek.jpg" alt="my image" style={{width: "75%", padding: "2rem", borderRadius: "2.5rem"}}/>
                 <h1 className="display-3" style={{fontSize: "2rem", fontWeight: "400"}}><a href="https://www.temasek.com.sg/en/index"><div className="linkPage">Temasek Holdings</div></a></h1>
                 <p>(May 2020 - Present)</p>
                 <p className="lead" style={{fontSize: "1rem",fontWeight: "400"}} >Incoming Software Engineering Intern</p>
                 <p style= {{fontSize: '0.9rem'}}>Part of the Temasek Digital Team</p>   
                 <p className="lead">
                </p>
              </div>
          </div>
      </div>
      <div class="gradient-border" id="box">Animated Gradient Border</div>
      <style jsx>{`

            .mainContainer {
                text-align: center;
                padding-bottom: 1rem;
                border-style: groove;
                margin: 0 1rem;
                align-items: center;
                margin-bottom: 5rem;
            }
            
           @media (max-width: 500px) {
                    .jumboContainer {
                    width: 90vw;
                    font-family: "Roboto";
                    }

                    .mainContainer {
                        margin-bottom: 5vh;
                    }

                }
            @media (min-width: 500px) {
                .jumboContainer {
                width: 60vw;
                font-family: "Roboto";
                width: 70vw;
                }
                
                .mainContainer {
                    margin: 0 10vw;
                    margin-bottom: 5vh;
                    
                }
                .linkPage {
                    text-decoration: none;
                    color: #3377CC;
                }

                .linkPage:hover {
                    transform: scale(1.15);
                    transition-duration: 0.5s;
                }

                #box {
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    width: 400px;
                    height: 200px;
                    color: white;
                    font-family: 'Raleway';
                    font-size: 2.5rem;
                  }
                  .gradient-border {
                    --borderWidth: 3px;
                    background: #1D1F20;
                    position: relative;
                    border-radius: var(--borderWidth);
                  }
                  .gradient-border:after {
                    content: '';
                    position: absolute;
                    top: calc(-1 * var(--borderWidth));
                    left: calc(-1 * var(--borderWidth));
                    height: calc(100% + var(--borderWidth) * 2);
                    width: calc(100% + var(--borderWidth) * 2);
                    background: linear-gradient(60deg, #f79533, #f37055, #ef4e7b, #a166ab, #5073b8, #1098ad, #07b39b, #6fba82);
                    border-radius: calc(2 * var(--borderWidth));
                    z-index: -1;
                    animation: animatedgradient 3s ease alternate infinite;
                    background-size: 300% 300%;
                  }
                  
                  
                  @keyframes animatedgradient {
                      0% {
                          background-position: 0% 50%;
                      }
                      50% {
                          background-position: 100% 50%;
                      }
                      100% {
                          background-position: 0% 50%;
                      }
                  }

            }
        `} </style>
        
    </div>
  );
};

export default JumboCard;
