import React from 'react';

const ExperienceCard = (props) => {

  return (
      <div style={{textAlign:"center", fontFamily:"Roboto"}}>
            <div className="mainContainer">
                <div className="imageContainer">
                    <img src="/saturday_kids.png"></img>
                </div>
                <div className="subContainer">
                    <a target="_blank"  className="company" href={"www.facebook.com"}>Saturday Kids</a>
                    <p className="jobTitle">Python Instructor</p>
                    <p className="country">Singapore, SG</p>
                    <p className="jobDescription">Selected as a Python Instructor for the Bukit Panjang library students. In collaboration with Google and IMDA, I led a group of assistants to teach a class of 18 students.</p>
                </div>
          </div>
        

    <style jsx>{`
        .mainContainer {
            display: flex;
            border-style: dashed;
        }
        .subContainer {
            display: flex;
            flex-direction: column;         
        }

        .container {
            display:inline-block;
        }

        img {
            border-radius: 20px;
        }

        .company {
            font-size: 2rem;
            color: #3377cc;
            font-weight: 500;
        }

        .jobTitle {
            font-size: 1.5rem;
            color: #5e9516;
            font-weight: 500;

        }

        .country {
            color: #50555c;
            font-weight: 400;
        }

        a:hover {
            color: tomato;
            text-decoration: none;
            transition-duration: 0.5s            
        }

        @media (max-width: 768px) {
            .mainContainer {
                margin: 5vh 5vw;
                display: flex;
                flex-direction: column

            }
            
            img {
                max-width: 80vw
            }
        }

        @media (min-width: 768px) {
            .mainContainer {
                margin: 5vh auto;
                max-width: 50vw
            }

            .subContainer {
                text-align: left;
                height: 30vh
            }

            .imageContainer {
                display: flex;
                flex-direction: inline-flex;
                align-items: center;
            }

            img {
                max-height: 30vh
            }
            
            .company {
                padding-left: 2rem
            }

            .jobTitle {
                padding-left: 2rem
            }

            .country {
                padding-left: 2rem
            }  

            .jobDescription {
                padding-left: 2rem
            }
        }
    `} </style>
      </div>
    
            
  );
};

export default ExperienceCard;
