require('isomorphic-fetch');
import ReactTable from "react-table"

var values = []

const query = `
query {
  user(login: "marcusfooo") {
    repositories(first: 100, isFork: false) {
      nodes {
        name
        description
        url
        primaryLanguage {name}
      }
    }
  }
}
`;

var url = "https://api.github.com/graphql";
auth = "fdb88caccccfab0e29dea3ee4843d6dd6fe48b1b"
var values = []



export class GithubTable extends React.Component {
  constructor() {
    super();
    this.state = {
      data: makeData()
    };
  }
  render() {
    const { data } = this.state;
    return (
      <div>
        <ReactTable
          data={data}
          columns={[
            {
              Header: "Name",
              columns: [
                {
                  Header: "First Name",
                  accessor: "firstName"
                },
                {
                  Header: "Last Name",
                  id: "lastName",
                  accessor: d => d.lastName
                }
              ]
            },
            {
              Header: "Info",
              columns: [
                {
                  Header: "Age",
                  accessor: "age"
                },
                {
                  Header: "Status",
                  accessor: "status"
                }
              ]
            },
            {
              Header: 'Stats',
              columns: [
                {
                  Header: "Visits",
                  accessor: "visits"
                }
              ]
            }
          ]}
          defaultPageSize={10}
          className="-striped -highlight"
        />
        <br />
        <Tips />
        <Logo />
      </div>
    );
  }
}
async function  fetchGithub(url,query,auth) {

let res;
  try {
    res = await fetch(url, {
      method: 'POST',
      headers: { 
        'Authorization': 'Bearer ' + auth,
        'Content-Type': 'application/json'     
      },
      body: JSON.stringify({ query: query }),
    })
  } catch (err) {
    console.log('Http error', err)
    return res.status(500).send()
  }
  
  res = await res.json()
  var values = await res.data.user.repositories.nodes
  console.log(values) 
}
