require('isomorphic-fetch');

var values = []

const query = `
query {
  user(login: "marcusfooo") {
    repositories(first: 100, isFork: false) {
      nodes {
        name
        description
        url
        primaryLanguage {name}
      }
    }
  }
}
`;

const url = "https://api.github.com/graphql";
auth = "fdb88caccccfab0e29dea3ee4843d6dd6fe48b1b"
var values = []
async function  fetchGithub(url,query,auth) {
  var res = await fetch(url, {
    method: 'POST',
    headers: { 
      'Authorization': 'Bearer ' + auth,
      'Content-Type': 'application/json'     
    },
    body: JSON.stringify({ query: query }),
  })
  console.log(res)
  var res = await res.json()
  for(var i=0; i< res.data.user.repositories.nodes.length; i++) {
    values.push([res[i].name,res[i].description,res[i].url,res[i].primaryLanguage]);
  }
  console.log(values)  
    
}


module.exports = {
  fetchGithub,
  url,
  query,
  auth
}
