import React from 'react';

const ExperienceCard = (props) => {

  return (
      <div style={{textAlign:"center", fontFamily:"Roboto"}}>
          <div className="container">
            <div className="mainContainer">
                <div className="imageContainer">
                    <img src="/saturday_kids.png"></img>
                </div>
                <div className="subContainer">
                    <p className="company">Saturday Kids</p>
                    <p className="jobTitle">Python Instructor</p>
                    <p className="country">Singapore, SG</p>
                    <p className="jobDescription">Selected as a Python Instructor for the Bukit Panjang library students. In collaboration with Google and IMDA, I led a group of assistants to teach a class of 18 students.</p>
                </div>
          </div>
        
        </div>

    <style jsx>{`
        .mainContainer {
            display: flex;
            background: red;
            margin: 5vh 0;
            

    }
        .subContainer {
            display: flex;
            flex-direction: column;         
        }

        .container {
            display:inline-block;
            align-content: space-around;
        }

        img {
            border-radius: 20px;
        }

        .company {
            font-size: 2.5rem
        }

        .jobTitle {
            font-size: 1.5rem
        }

        @media (max-width: 768px) {
            .mainContainer {
                flex-direction: column
            }
            
            img {
                max-width: 80vw
            }
        }

        @media (min-width: 768px) {
            .mainContainer {
                width: 50vw
            }

            .subContainer {
                text-align: left;
            }

            .imageContainer {
                display: flex;
                flex-direction: inline-flex;
                align-items: center;
            }

            img {
                max-height: 30vh
            }
            
            .company {
                padding: 1rem
            }

            .jobTitle {
                padding: 1rem
            }

            .country {
                padding: 1rem
            }  

            .jobDescription {
                padding: 1rem
            }
        }
    `} </style>
      </div>
    
            
  );
};

export default ExperienceCard;
