import React from 'react';
import { Table } from 'reactstrap';
import fetch from 'isomorphic-unfetch';



class ProjectTable extends React.Component{
  constructor(props) {
    super(props);
    this.state = { data: [{}]};
    this.fetchGithub = this.fetchGithub.bind(this)
  }
  componentDidMount(){
    this.fetchGithub()
 }
  fetchGithub() {    
    const query = `
    query {
      user(login: "marcusfooo") {
        repositories(first: 100, isFork: false) {
          nodes {
            name
            description
            url
          }
        }
      }
    }
    `;
  const url = "https://api.github.com/graphql";
  const auth = "fdb88caccccfab0e29dea3ee4843d6dd6fe48b1b"
  fetch(url, {
    method: 'POST',
    headers: { 
      'Authorization': 'Bearer ' + auth,
      'Content-Type': 'application/json'
      
    },
    body: JSON.stringify({ query: query }),
  })
    .then(res => res.json())
    .then(res => this.setState({data: res.data.user.repositories.nodes}))

}
  render() {
    console.log(this.state.data[0].url)
   
    return (
      <div style= {{maxWidth:"80vw", textAlign: "center"}}>
        <Table striped>
        <thead>
        <tr>
          <th>Repository Name</th>
          <th>Description</th>
          <th>URL</th>
        </tr>
      </thead>
        {this.state.data.map(row => <tr scope="row"><th scope="row"></th><td><a href target="_blank" href={row.url}>{row.name}</a></td><td>{row.description}</td><td>{row.url}</td></tr> )}
        </Table>
      </div>
      
  );
  }
  
}

export default ProjectTable;
