import React from 'react';

const ExperienceCard = (props) => {

  return (
      <div style={{textAlign:"center", fontFamily:"Roboto"}}>
          <div className="container">
            <div className="mainContainer">
                <div className="imageContainer">
                    <img src="/saturday_kids.png"></img>
                </div>
                <div className="subContainer">
                    <p className="company">Saturday Kids</p>
                    <p className="jobTitle">Python Instructor</p>
                    <p className="country">Singapore, SG</p>
                    <p className="jobDescription">Selected as a Python Instructor for the Bukit Panjang library students. In collaboration with Google and IMDA, I led a group of assistants to teach a class of 18 students.</p>
                </div>
          </div>
        
        </div>

    <style jsx>{`
        .mainContainer {
            display: flex;
            background: red;
            margin: 5vh 0;
    }
        .subContainer {
            display: flex;
            flex-direction: column;         
        }

        .container {
            display:inline-block;
        }

        .imageContainer {
            display: flex;
            flex-direction: inline-flex;
            align-items: center
            
        }
        img {
            border-radius: 20px;
            height: 80%;
        }

        .company {
            font-size: 2.5rem
        }

        .jobTitle {
            font-size: 1.5rem
        }

        @media (max-width: 768px) {
            .mainContainer {
                flex-direction: column
            }
            
            .imageContainer {
                display: flex;
                flex-direction: row;
                align-items: center;
            }

            img {
                max-width: 80vw
            }
        }

        @media (min-width: 768px) {
            .mainContainer {
                width: 65vw
            }

            .subContainer {
                text-align: left;
            }

            .imageContainer {
                display: flex;
                flex-direction: inline-flex;
                align-items: center;

            }
            
            .company {
                padding: 1rem
            }

            .jobTitle {
                padding: 1rem
            }

            .country {
                padding: 1rem
            }  

            .jobDescription {
                padding: 1rem
            }
        }
    `} </style>
      </div>
    
            
  );
};

export default ExperienceCard;
