import React from 'react';
import { Table } from 'reactstrap';
import fetch from 'isomorphic-unfetch';


class ProjectTable extends React.Component{
  constructor() {
    super();
    this.state = { data: []};
  }

  async componenetDidMount() {
    const query = `
    query {
      user(login: "marcusfooo") {
        repositories(first: 100, isFork: false) {
          nodes {
            name
            description
            url
            primaryLanguage {name}
          }
        }
      }
    }
    `;
  const url = "https://api.github.com/graphql";
  const auth = "fdb88caccccfab0e29dea3ee4843d6dd6fe48b1b"
  let res;  
  res = await fetch(url, {
    method: 'POST',
    headers: { 
      'Authorization': 'Bearer ' + auth,
      'Content-Type': 'application/json'     
    },
    body: JSON.stringify({ query: query }),
  })
  json = await res.json()
  console.log(json)
  this.setState({data: json})
  }
  render() {
    console.log(this.state.data)
    return (
      <div style={{textAlign:"center"}}> 
      <div style={{display: "inline-block"}}>
      <Table bordered style={{width:"80vw"}}>
      <thead>
        <tr>
          <th>#</th>
          <th>Name</th>
          <th>Description</th>
          <th>Language</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <th scope="row">1</th>
          <td>Mark</td>
          <td>Otto</td>
          <td>@mdo</td>
        </tr>
        <tr>
          <th scope="row">2</th>
          <td>Jacob</td>
          <td>Thornton</td>
          <td>@fat</td>
        </tr>
        <tr>
          <th scope="row">3</th>
          <td>Larry</td>
          <td>the Bird</td>
          <td>@twitter</td>
        </tr>
      </tbody>
    </Table>
      </div>
            
      </div>
    
  );
  }
  
}

export default ProjectTable;
