import Head from 'next/head'
import image_hero from '../resources/Hero.jpg'

export default function Hero() {
    return (      
        <div class="hero-image">
            <div class="hero-text">
            <h1>I am John Doe</h1>
            <p>And I'm a Photographer</p>
            <button>Hire me</button>
            </div>
            <style jsx>{`
                .hero-image {
                    background-image: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url("../resources/Hero.jpg");
                    height: 50%;
                    background-position: center;
                    background-repeat: no-repeat;
                    background-size: cover;
                    position: relative;
                }

                .hero-text {
                    text-align: center;
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%);
                    color: white;
                  }
            `} </style>
        </div>
    );
  }
