import React from 'react';
import Head from 'next/head'

const JumboCard = (props) => {
  return (
    <div className="mainContainer">
      <div className="jumboContainer" style={{display: "inline-block", background: "white"}}>
          <div>
              <div>
              <img src="/Temasek.jpg" alt="my image" style={{width: "75%", padding: "2rem", borderRadius: "2.5rem"}}/>
                 <h1 className="display-3" style={{fontSize: "2rem", fontWeight: "400"}}><a href="https://www.temasek.com.sg/en/index"><div className="linkPage">Temasek Holdings</div></a></h1>
                 <p>(May 2020 - Present)</p>
                 <p className="lead" style={{fontSize: "1rem",fontWeight: "400"}} >Incoming Software Engineering Intern</p>
                 <p style= {{fontSize: '0.9rem'}}>Part of the Temasek Digital Team</p>   
                 <p className="lead">
                </p>
              </div>
          </div>
          .bb
      </div>
      
      <style jsx>{`

            .mainContainer {
                text-align: center;
                padding-bottom: 1rem;
                border-style: groove;
                margin: 0 1rem;
                align-items: center;
                margin-bottom: 5rem;
            }
            
           @media (max-width: 500px) {
                    .jumboContainer {
                    width: 90vw;
                    font-family: "Roboto";
                    }

                    .mainContainer {
                        margin-bottom: 5vh;
                    }

                }
            @media (min-width: 500px) {
                .jumboContainer {
                width: 60vw;
                font-family: "Roboto";
                width: 70vw;
                }
                
                .mainContainer {
                    margin: 0 10vw;
                    margin-bottom: 5vh;
                    
                }
                .linkPage {
                    text-decoration: none;
                    color: #3377CC;
                }

                .linkPage:hover {
                    transform: scale(1.15);
                    transition-duration: 0.5s;
                }

                $anime-time: 8s;

$box-size: 300px;
$clip-distance: .05;
$clip-size: $box-size * (1 + $clip-distance * 2);
$path-width: 2px;

$main-color: red;

$codepen-logo-path: url('//blog.codepen.io/wp-content/uploads/2012/06/Button-White-Large.png');

%full-fill {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
}

.bb {
  @extend %full-fill;
  width: $box-size;
  height: $box-size;
  margin: auto;
  background: $codepen-logo-path no-repeat 50% / 70% rgba(#000, .1);
  color: $main-color;
  box-shadow: inset 0 0 0 1px rgba($main-color, .5);
}

.bb::before, .bb::after {
    @extend %full-fill;
    content: '';
    z-index: -1;
    margin: -1 * $clip-distance * 100%;
    box-shadow: inset 0 0 0 $path-width; 
    animation: clipMe $anime-time linear infinite;
}

.bb::before {
    animation-delay: $anime-time * -.5;
  }



            }
        `} </style>
    </div>
  );
};

export default JumboCard;
