import React from 'react';

const ExperienceCard = (props) => {

  return (
      <div style={{textAlign:"center", fontFamily:"Roboto"}}>
          <div className="container">
            <div className="mainContainer">
                <div className="imageContainer">
                    <img src="/saturday_kids.png"></img>
                </div>
                <div className="subContainer">
                    <h1>Saturday Kids</h1>
                    <h2>Python Instructor</h2>
                    <p>Selected as a Python Instructor for the Bukit Panjang library students. In collaboration with Google and IMDA, I led a group of assistants to teach a class of 18 students.</p>
                </div>
          </div>
        
        </div>

    <style jsx>{`
        .mainContainer {
            display: flex;
            background: red;
            margin: 5vh 0;
    }
        .subContainer {
            display: flex;
            flex-direction: column;
        }

        .container {
            display:inline-block;
        }
        img {
            border-radius: 20px;
            max-width: 50vw;
        }

        @media (max-width: 768px) {
            .mainContainer {
                flex-direction: column
            }

            img {
                max-width: 80vw
            }
        }

        @media (min-width: 768px) {
            .mainContainer {
                max-width: 100vw
            }
            
            h1 {
                padding: 5%
            }

            h2 {

            }
        }
    `} </style>
      </div>
    
            
  );
};

export default ExperienceCard;
