import Head from 'next/head'

export default function Hero() {
    return (      
        <div className="hero-image">
            <img src="/Hero.jpg" alt="my image" />
            <div className="hero-text">
                <h1>Marcus Foo</h1>
                <p>And I'm an aspiring Software Engineer</p>
            </div>
            <style jsx>{`
                .hero-image {                  
                    background-position: center;
                    background-repeat: no-repeat;
                    background-size: cover;
                    position: relative;
                }

                img {
                    overflow-x: hidden;
                    width:100vw;
                    filter: blur(8px);
                    height: 85vh
                }

                .hero-text {
                    text-align: center;
                    position: absolute;

                    transform: translate(-50%, -50%);
                    color: white;
                  }

                  h1 {
                      font-size:4rem;
                  }

                  p {
                      font-size: 2rem;
                  }
            `} </style>
        </div>
    );
  }
